import React, { useEffect, useRef } from 'react';
import './App.css';
import HeroTransformSection from './components/HeroTransformSection';

function App() {
  const orb1Ref = useRef(null);
  const orb2Ref = useRef(null);
  const orb3Ref = useRef(null);
  const orb4Ref = useRef(null);

  useEffect(() => {
    const handleMouseMove = (e) => {
      const { clientX: mouseX, clientY: mouseY } = e;
      const { innerWidth: windowWidth, innerHeight: windowHeight } = window;

      // Calculate mouse position as percentage
      const xPercent = (mouseX / windowWidth) * 100;
      const yPercent = (mouseY / windowHeight) * 100;

      // Apply different movement intensities for each orb
      if (orb1Ref.current) {
        const moveX = (xPercent - 50) * 0.5; // 50% intensity
        const moveY = (yPercent - 50) * 0.3;
        orb1Ref.current.style.transform = `translate(${moveX}px, ${moveY}px)`;
      }

      if (orb2Ref.current) {
        const moveX = (xPercent - 50) * -0.3; // Opposite direction, 30% intensity
        const moveY = (yPercent - 50) * 0.4;
        orb2Ref.current.style.transform = `translate(${moveX}px, ${moveY}px)`;
      }

      if (orb3Ref.current) {
        const moveX = (xPercent - 50) * 0.4; // 40% intensity
        const moveY = (yPercent - 50) * -0.2; // Opposite Y direction
        orb3Ref.current.style.transform = `translate(${moveX}px, ${moveY}px)`;
      }

      if (orb4Ref.current) {
        const moveX = (xPercent - 50) * -0.2; // Opposite direction, 20% intensity
        const moveY = (yPercent - 50) * -0.3; // Opposite Y direction
        orb4Ref.current.style.transform = `translate(${moveX}px, ${moveY}px)`;
      }
    };

    // Add mouse move listener
    window.addEventListener('mousemove', handleMouseMove);

    // Cleanup
    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
    };
  }, []);

  return (
    <div className="App">
      {/* Animated Background Glow Elements */}
      <div className="glow-container">
        <div ref={orb1Ref} className="glow-orb glow-orb-1 cursor-responsive"></div>
        <div ref={orb2Ref} className="glow-orb glow-orb-2 cursor-responsive"></div>
        <div ref={orb3Ref} className="glow-orb glow-orb-3 cursor-responsive"></div>
        <div ref={orb4Ref} className="glow-orb glow-orb-4 cursor-responsive"></div>
      </div>

      <HeroTransformSection />
    </div>
  );
}

export default App;
