import React, { useState, useEffect } from 'react';
import webpageBg from '../assets/Webpage.png';
import calendarIcon from '../assets/hero-section/calendar.png';
import clockIcon from '../assets/hero-section/clock.png';
import speaker1 from '../assets/hero-section/speaker1.png';
import speaker2 from '../assets/hero-section/speaker2.png';
import speaker3 from '../assets/hero-section/speaker3.png';
import aiIcon from '../assets/web-agenda/artificial-intelligence-04.png';
import sparklesIcon from '../assets/web-agenda/sparkles.png';
import noteDoneIcon from '../assets/web-agenda/note-done.png';
import addToListIcon from '../assets/web-agenda/add-to-list.png';
import arrowMoveIcon from '../assets/web-agenda/arrow-move-down-right.png';

const HeroTransformSection = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [isFormFocused, setIsFormFocused] = useState(false);
  const [hoveredItem, setHoveredItem] = useState(null);

  useEffect(() => {
    const timer = setTimeout(() => setIsVisible(true), 200);
    return () => clearTimeout(timer);
  }, []);

  const agendaItems = [
    { icon: sparklesIcon, text: "Post-AI delivery standards you should demand from vendors", alt: "Sparkles" },
    { icon: noteDoneIcon, text: "How to evaluate vendor AI claims with practical tests", alt: "Checklist" },
    { icon: aiIcon, text: "AI-readiness assessment for your Salesforce org", alt: "AI Chip" },
    { icon: addToListIcon, text: "EXO's 3-step process for AI-speed delivery", alt: "Add to list" },
    { icon: arrowMoveIcon, text: "Real case studies of 3-10x delivery acceleration", alt: "10x arrow" }
  ];

  const problemItems = [
    {
      title: "Strategic Initiatives Stall in Technical Complexity",
      description: "High-value business priorities get buried under system maintenance.",
      margin: "ml-28"
    },
    {
      title: "AI Mandates Demand Faster Execution",
      description: "Leadership expects AI-accelerated results, but legacy processes can't keep pace.",
      margin: "ml-16"
    },
    {
      title: "Elite Teams Spend 80% on Routine Work",
      description: "Your most capable technical talent spends 80% of time on repetitive tasks instead of breakthrough features.",
      margin: "ml-4"
    }
  ];

  return (
    <section className="relative min-h-screen overflow-hidden">
      {/* Background Image */}
      <div className="absolute inset-0">
        <img src={webpageBg} alt="Webpage Background" className="w-full h-full object-cover object-center" />
      </div>

      {/* Hero Content */}
      <div className="relative z-10 flex min-h-screen">
        {/* Left Section - Webinar Details & Speakers */}
        <div className="flex-1 flex flex-col justify-start pt-40 ml-20 px-8">
          <div className={`max-w-2xl transition-all duration-1000 ${isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-12'}`}>
            {/* Webinar Introduction */}
            <p className="text-white text-lg font-normal mb-8 animate-pulse">
            Join us for an exclusive webinar
            </p>

            {/* Main Title */}
            <h1 className="text-white text-5xl font-normal mb-4 animate-pulse">
              AI-Ready Salesforce
            </h1>

            {/* Sub-headline with underline */}
            <div className="mb-8">
              <h2 className="text-white text-3xl font-normal animate-pulse">
                Transform Your Delivery Speed
              </h2>
              <div className="delivery-underline w-48 animate-pulse"></div>
            </div>

            {/* Date and Time */}
            <div className={`flex flex-col space-y-4 mb-12 transition-all duration-700 ${isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-8'}`} style={{ transitionDelay: '300ms' }}>
              <div className="flex items-center space-x-2 hover:scale-105 transition-transform duration-300">
                <img src={calendarIcon} alt="Calendar" className="w-6 h-6 animate-bounce" />
                <span className="text-white text-xl">August 13, 2025</span>
              </div>
              <div className="flex items-center space-x-2 hover:scale-105 transition-transform duration-300">
                <img src={clockIcon} alt="Clock" className="w-6 h-6 animate-bounce" />
                <span className="text-white text-xl">2:00 PM SGT (45-min)</span>
              </div>
            </div>

            {/* Speakers Section */}
            <div className={`flex space-6 transition-all duration-700 ${isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-8'}`} style={{ transitionDelay: '600ms' }}>
              {/* Speaker 1 - Heather */}
              <div className="w-[224px] h-[84px] flex items-center space-x-4 hover:scale-105 transition-transform duration-300">
                <img src={speaker3} alt="Heather Mao" className="w-[84px] h-[84px] rounded-full flex-shrink-0 hover:ring-4 hover:ring-blue-400 transition-all duration-300" />
                <div className="w-[124px] h-[74px] text-left mt-2">
                  <p className="text-white text-sm font-semibold whitespace-nowrap">Heather Mao</p>
                  <p className="text-white text-xs opacity-80">
                    Strategic Account<br />
                    Director - Agentforce<br />
                    Salesforce
                  </p>
                </div>
              </div>

              {/* Speaker 2 - Sidu */}
              <div className="w-[224px] h-[84px] flex items-center space-x-4 ml-4 hover:scale-105 transition-transform duration-300">
                <img src={speaker1} alt="Sidu Ponnapa" className="w-[84px] h-[84px] rounded-full flex-shrink-0 hover:ring-4 hover:ring-blue-400 transition-all duration-300" />
                <div className="w-[124px] h-[74px] text-left mt-2">
                  <p className="text-white text-sm font-semibold whitespace-nowrap">Sidu Ponnapa</p>
                  <p className="text-white text-xs opacity-80">CEO & Co-founder, realfast</p>
                </div>
              </div>

              {/* Speaker 3 - Aakash */}
              <div className="w-[224px] h-[84px] flex items-center space-x-4 hover:scale-105 transition-transform duration-300">
                <img src={speaker2} alt="Aakash Dharmadhikari" className="w-[84px] h-[84px] rounded-full flex-shrink-0 hover:ring-4 hover:ring-blue-400 transition-all duration-300" />
                <div className="w-[124px] h-[74px] text-left mt-2">
                  <p className="text-white text-sm font-semibold">Aakash Dharmadhikari</p>
                  <p className="text-white text-xs opacity-80">Co-Founder, realfast</p>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Right Section - Registration Form */}
        <div className={`flex-1 flex items-start justify-center px-8 pt-40 transition-all duration-1000 ${isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-12'}`} style={{ transitionDelay: '900ms' }}>
          <div className={`w-full max-w-sm relative transition-all duration-500 ${isFormFocused ? 'scale-105' : ''}`}>
            <div className="absolute inset-0 rounded-2xl opacity-32 p-[0.5px]">
              <div className="w-full h-full bg-black/40 rounded-2xl"></div>
            </div>
            <div className="relative z-10 p-8">
              <form className="space-y-8">
                {/* Full Name Field */}
                <div>
                  <input
                    type="text"
                    id="fullName"
                    placeholder="Full Name"
                    className="w-full px-4 py-4 bg-gray-900/50 border border-gray-600/50 rounded-xl text-white placeholder-gray-400 focus:outline-none focus:border-blue-500 transition-all duration-300 text-sm hover:border-blue-400"
                    onFocus={() => setIsFormFocused(true)}
                    onBlur={() => setIsFormFocused(false)}
                  />
                </div>

                {/* Email Field */}
                <div>
                  <input
                    type="email"
                    id="email"
                    placeholder="Email ID"
                    className="w-full px-4 py-4 bg-gray-900/50 border border-gray-600/50 rounded-xl text-white placeholder-gray-400 focus:outline-none focus:border-blue-500 transition-all duration-300 text-sm hover:border-blue-400"
                    onFocus={() => setIsFormFocused(true)}
                    onBlur={() => setIsFormFocused(false)}
                  />
                </div>

                {/* Register Button */}
                <button
                  type="submit"
                  className="w-full bg-[#1D4ED8] hover:bg-blue-800 text-white font-bold py-4 px-6 rounded-xl transition-all duration-300 text-sm hover:scale-105 hover:shadow-lg hover:shadow-blue-500/25"
                >
                  Register Now
                </button>
              </form>

              {/* Disclaimer */}
              <p className="text-gray-300 text-xs text-center mt-8 leading-relaxed">
                This webinar is co-presented with Salesforce and limited to 100 senior technical and business leaders.
              </p>
            </div>
          </div>
        </div>
      </div>

      {/* Transform Section */}
      <div className="relative py-20">
        <div className="relative z-10 h-96 flex items-center justify-center px-4">
          <div className={`text-center text-white mt-10 max-w-4xl transition-all duration-1000 ${isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-12'}`}>
            {/* Main Headline */}
            <h2 className="text-3xl md:text-3xl lg:text-3xl font-normal mb-6 leading-tight animate-pulse">
              Transform never-ending<br />
              roadmaps into week-long wins
            </h2>

            {/* Underline with arrow */}
            <div className="flex justify-center mb-8">
              <div className="w-32 h-1 bg-blue-400 rounded-full relative animate-pulse">
                <div className="absolute right-0 top-0 w-0 h-0 border-l-8 border-l-blue-400 border-t-4 border-t-transparent border-b-4 border-b-transparent animate-bounce"></div>
              </div>
            </div>

            {/* Sub-headline */}
            <p className="text-xl md:text-xl text-gray-300 font-normal leading-relaxed">
              EXO bridges the gap between AI-speed demands and legacy delivery processes.
            </p>
          </div>
        </div>
      </div>

      {/* Webinar Agenda Section */}
      <div className="relative py-40 px-4 md:px-8">
        <div className="max-w-4xl mx-auto text-white">
          {/* 2x3 Grid */}
          <div className="grid grid-cols-1 md:grid-cols-3">
            {/* Grid Item 1 - Webinar Agenda Title */}
            <div className={`p-6 md:p-8 border-b border-gray-700/40 border-r border-gray-700/40 transition-all duration-700 ${isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-8'}`}>
              <div className="h-full flex flex-col justify-center">
                <p className="text-sm text-gray-400 font-normal mb-3">Webinar Agenda</p>
                <h2 className="text-xl md:text-2xl font-medium leading-snug text-white/64">
                  Why traditional IT delivery models are failing in the AI era
                </h2>
              </div>
            </div>

            {/* Agenda Items with staggered animations */}
            {agendaItems.map((item, index) => (
              <div 
                key={index}
                className={`p-6 md:p-8 border-gray-700/40 border-r border-gray-700/40 border-b border-gray-700/40 transition-all duration-700 hover:bg-gray-800/20 hover:scale-105 cursor-pointer ${isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-8'}`}
                style={{ transitionDelay: `${(index + 1) * 200}ms` }}
                onMouseEnter={() => setHoveredItem(index)}
                onMouseLeave={() => setHoveredItem(null)}
              >
                <div className="flex flex-col items-start gap-4">
                  <img 
                    src={item.icon} 
                    alt={item.alt} 
                    className={`w-10 h-10 transition-all duration-300 ${hoveredItem === index ? 'scale-110 rotate-12' : ''}`} 
                  />
                  <p className="text-base text-gray-400 leading-snug transition-colors duration-300 hover:text-white">
                    {item.text}
                  </p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Problem Section */}
      <div className="relative py-40 px-16">
        <div className="max-w-6xl mx-auto text-white">
          {/* Section Title */}
          <div className={`text-center mb-20 transition-all duration-700 ${isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-8'}`}>
            <h2 className="text-lg font-normal text-[#BFDBFE] mb-4 animate-pulse">The Problem</h2>
            <h1 className="text-3xl md:text-3xl font-normal leading-tight">
              Leadership Expects AI Results. Teams Deliver at Legacy Speed.
            </h1>
          </div>

          {/* Three Column Layout */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 md:gap-8 justify-items-start">
            {problemItems.map((item, index) => (
              <div 
                key={index}
                className={`${item.margin} text-left max-w-[240px] transition-all duration-700 hover:scale-105 ${isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-8'}`}
                style={{ transitionDelay: `${(index + 6) * 200}ms` }}
              >
                <div className="flex justify-start mb-6">
                  <div className="w-28 h-0.5 bg-gradient-to-r from-[#362CFF] via-[#BFDBFE] to-[#92A7FF] animate-pulse"></div>
                </div>
                <h3 className="text-xl md:text-xl font-normal mb-8 text-left hover:text-blue-300 transition-colors duration-300">
                  {item.title}
                </h3>
                <p className="text-gray-300 text-base font-normal leading-relaxed text-left hover:text-white transition-colors duration-300">
                  {item.description}
                </p>
              </div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default HeroTransformSection;
