import React from 'react';

const ImageSection = () => {
  return (
    <section className="relative h-screen overflow-hidden bg-gradient-to-br from-black via-blue-900 to-purple-900">
      {/* Glowing Lines Effect */}
      <div className="absolute inset-0">
        {/* Left side horizontal lines */}
        <div className="absolute left-0 top-1/4 w-32 h-px bg-gradient-to-r from-blue-400 to-transparent opacity-60"></div>
        <div className="absolute left-0 top-1/3 w-24 h-px bg-gradient-to-r from-blue-400 to-transparent opacity-40"></div>
        <div className="absolute left-0 top-1/2 w-28 h-px bg-gradient-to-r from-blue-400 to-transparent opacity-50"></div>

        {/* Right side converging lines */}
        <div className="absolute right-0 top-1/4 w-48 h-px bg-gradient-to-l from-blue-400 to-transparent opacity-60 transform rotate-12"></div>
        <div className="absolute right-0 top-1/3 w-40 h-px bg-gradient-to-l from-blue-400 to-transparent opacity-40 transform rotate-8"></div>
        <div className="absolute right-0 top-1/2 w-44 h-px bg-gradient-to-l from-blue-400 to-transparent opacity-50 transform rotate-4"></div>
      </div>

      {/* Content Overlay */}
      <div className="relative z-10 h-full flex items-center justify-center px-4">
        <div className="text-center text-white max-w-4xl">
          {/* Main Headline */}
          <h2 className="text-4xl md:text-5xl lg:text-6xl font-bold mb-6 leading-tight">
            Transform never-ending<br />
            roadmaps into week-long wins
          </h2>

          {/* Underline with arrow */}
          <div className="flex justify-center mb-8">
            <div className="w-32 h-1 bg-blue-400 rounded-full relative">
              <div className="absolute right-0 top-0 w-0 h-0 border-l-8 border-l-blue-400 border-t-4 border-t-transparent border-b-4 border-b-transparent"></div>
            </div>
          </div>

          {/* Sub-headline */}
          <p className="text-xl md:text-2xl text-gray-300 leading-relaxed">
            EXO bridges the gap between AI-speed demands and legacy delivery processes.
          </p>
        </div>
      </div>
    </section>
  );
};

export default ImageSection;
